import { __decorate } from "tslib";
import { Component } from '@angular/core';
let PokemonListComponent = class PokemonListComponent {
    //Inicializacion/llamada del servicio
    constructor(RequestService) {
        this.RequestService = RequestService;
        this.pokemonList = [];
        this.pokemonFilteredList = []; //lista filtrada
        this.baseURL = 'https://pokeapi.co/api/v2/pokemon/?limit=';
        this.limit = 151; //primera generacion
        this.RequestService.getCharacters(this.baseURL + this.limit).
            subscribe(async (data) => {
            const results = data.results;
            let pokemon = [];
            for (let pokeIterable in results) {
                let id = pokeIterable['url'].split('/');
                await pokemon.push(await fetch(pokemon['url']).then(async (response) => response.json())
                    .then(async (data) => {
                    return { id: Number(id[id.length - 2]),
                        name: pokemon['name'],
                        url: pokemon['url'],
                        image: data['sprites']['front_default'] };
                }));
            }
            this.pokemonList = pokemon;
            this.pokemonFilteredList = this.pokemonList;
        });
    }
    ngOnInit() {
        callRequestService(this.baseURL + '25');
    }
    onChangeFilter(filter) {
        const newList = this.pokemonList.filter(pokemon => pokemon.name.toLowerCase().includes(filter.trim().toLowerCase()));
        this.pokemonFilteredList = newList;
    }
};
PokemonListComponent = __decorate([
    Component({
        selector: 'app-pokemon-list',
        templateUrl: './pokemon-list.component.html',
        styleUrls: ['./pokemon-list.component.scss']
    })
], PokemonListComponent);
export { PokemonListComponent };
function callRequestService(url) {
    //getCharacters oara guardar los resultados
    this.RequestService.getCharacters(url).subscribe(async (data) => {
        const results = data.results;
        let pokemon = [];
        for (let pokeIterable in results) {
            let id = pokeIterable['url'].split('/');
            await pokemon.push(await fetch(pokemon['url']).then(async (response) => response.json())
                .then(async (data) => {
                return { id: Number(id[id.length - 2]),
                    name: pokemon['name'],
                    url: pokemon['url'],
                    image: data['sprites']['front_default'] };
            }));
        }
        this.PokemonResponseInterface = data;
        this.PokemonResponseInterface.results = pokemon;
    });
}
